import fetch from 'isomorphic-unfetch';

const Post = (props) => {
    const { post } = props;
    return (
        <div>{post}</div>
    );
}

Post.getInitialProps = async (context) => {
    // console.warn("context", context);
    const postId = context.query.id;
    const res = await fetch(`https://codeytek.com/wp-json/wp/v2/posts?id=${postId}`);
    const postData = res.json();

    return {
        post: postData
    }
}

export default Post;
