import fetch from 'isomorphic-unfetch';

const Post = () => {
    return (
        <div>Single post</div>
    );
}

Post.getInitialProps = async (context) => {
    console.warn("context", context);
    const res = await fetch(`https://codeytek.com/wp-json/wp/v2/posts?id=${context.query.id}`);
}

export default Post;
